import React, { useState } from 'react';
import styles from './NoteList.module.css';

function NoteList({ notes, selectedNote, onNoteSelect, onNewNote, searchTerm, onSearchChange, onNoteFavorite, onNoteDelete, onNoteRestore, onNotePermanentDelete }) {
  const [openMenuId, setOpenMenuId] = useState(null);

  const filteredNotes = notes.filter(note =>
    note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    note.content.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  const toggleMenu = (e, noteId) => {
    e.stopPropagation();
    setOpenMenuId(openMenuId === noteId ? null : noteId);
  };

  const handleMenuAction = (e, action, noteId) => {
    e.stopPropagation();
    action(noteId);
    setOpenMenuId(null);
  };

  return (
    <div className={styles.noteList}>
      <div className={styles.header}>
        <input
          type="text"
          placeholder="Search notes..."
          value={searchTerm}
          onChange={(e) => onSearchChange(e.target.value)}
          className={styles.searchInput}
        />
        <button onClick={onNewNote} className={styles.newNoteButton}>
          + New Note
        </button>
      </div>

      <div className={styles.notes}>
        {filteredNotes.length === 0 ? (
          <div className={styles.emptyState}>
            <p>No notes found</p>
          </div>
        ) : (
          filteredNotes.map(note => (
            <div
              key={note.id}
              className={`${styles.noteItem} ${
                selectedNote?.id === note.id ? styles.selected : ''
              }`}
            >
              <div 
                className={styles.noteContent}
                onClick={() => onNoteSelect(note)}
              >
                <div className={styles.noteTitle}>
                  {note.title || 'Untitled'}
                  {note.isFavorite && <span className={styles.star}>‚òÖ</span>}
                </div>
                <div className={styles.notePreview}>
                  {note.content.substring(0, 100) || 'No content'}
                </div>
                <div className={styles.noteDate}>
                  {formatDate(note.updatedAt)}
                </div>
              </div>

              <div className={styles.noteActions}>
                {!note.isDeleted ? (
                  <>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onNoteFavorite(note.id);
                      }}
                      className={`${styles.actionButton} ${note.isFavorite ? styles.favorite : ''}`}
                      title={note.isFavorite ? "Remove from favorites" : "Add to favorites"}
                    >
                      ‚òÖ
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onNoteDelete(note.id);
                      }}
                      className={styles.actionButton}
                      title="Move to trash"
                    >
                      üóëÔ∏è
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onNoteRestore(note.id);
                      }}
                      className={styles.actionButton}
                      title="Restore note"
                    >
                      ‚Ü©Ô∏è
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        onNotePermanentDelete(note.id);
                      }}
                      className={styles.actionButton}
                      title="Delete permanently"
                    >
                      ‚ùå
                    </button>
                  </>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default NoteList;
