import React, { useState } from 'react';
import { Button } from '../../atoms';
import { SettingItem } from '../../molecules';
import styles from './Settings.module.css';

const Settings = ({ 
  isOpen = false,
  onClose,
  settings = {},
  onSettingToggle,
  onDownloadNotes,
  isDarkTheme = false,
  className = ''
}) => {
  const [activeSection, setActiveSection] = useState('preferences');

  if (!isOpen) return null;

  const renderContent = () => {
    switch (activeSection) {
      case 'preferences':
        return (
          <>
            <h2>Preferences</h2>
            <div className={styles.settingGroup}>
              <SettingItem
                title="Display line numbers"
                description="Controls whether the editor should display line numbers"
                checked={settings.displayLineNumbers}
                onChange={() => onSettingToggle('displayLineNumbers')}
                className={`${isDarkTheme ? 'darkTheme' : ''}`}
              />
              <SettingItem
                title="Markdown preview"
                description="Controls whether markdown preview mode is enabled"
                checked={settings.markdownPreview}
                onChange={() => onSettingToggle('markdownPreview')}
                className={`${isDarkTheme ? 'darkTheme' : ''}`}
              />
            </div>
          </>
        );
      case 'data':
        return (
          <>
            <h2>Data Management</h2>
            <div className={styles.settingGroup}>
              <div className={styles.actionItem}>
                <div className={styles.actionInfo}>
                  <h3>Download all notes</h3>
                  <p>Export all your notes as a single markdown file</p>
                </div>
                <Button
                  onClick={onDownloadNotes}
                  variant="outline"
                  size="small"
                >
                  Download
                </Button>
              </div>
            </div>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className={`${styles.settingsOverlay} ${isDarkTheme ? styles.darkTheme : ''} ${className}`}>
      <div className={styles.settingsModal}>
        <div className={styles.settingsHeader}>
          <h1>Settings</h1>
          <Button
            onClick={onClose}
            variant="ghost"
            size="small"
            className={styles.closeButton}
          >
            âœ•
          </Button>
        </div>

        <div className={styles.settingsBody}>
          <div className={styles.settingsSidebar}>
            <Button
              onClick={() => setActiveSection('preferences')}
              variant={activeSection === 'preferences' ? 'primary' : 'ghost'}
              className={styles.sidebarButton}
            >
              Preferences
            </Button>
            <Button
              onClick={() => setActiveSection('data')}
              variant={activeSection === 'data' ? 'primary' : 'ghost'}
              className={styles.sidebarButton}
            >
              Data
            </Button>
          </div>

          <div className={styles.settingsContent}>
            {renderContent()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
