import React, { useState, useEffect } from 'react';

const LocalStorageTest = () => {
  const [status, setStatus] = useState('Testing...');
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Test localStorage
    try {
      // Test basic localStorage functionality
      localStorage.setItem('test', 'working');
      const testResult = localStorage.getItem('test');
      localStorage.removeItem('test');

      if (testResult === 'working') {
        setStatus('‚úÖ localStorage is working');

        // Check if notes exist
        const savedNotes = localStorage.getItem('takenote-notes');
        if (savedNotes) {
          const parsed = JSON.parse(savedNotes);
          setNotes(parsed);
          setStatus(`‚úÖ localStorage working - ${parsed.length} notes found`);
        } else {
          setStatus('‚úÖ localStorage working - No notes found');
        }
      } else {
        setStatus('‚ùå localStorage test failed');
      }
    } catch (error) {
      setStatus(`‚ùå localStorage error: ${error.message}`);
    }
  }, []);

  const clearStorage = () => {
    localStorage.removeItem('takenote-notes');
    setNotes([]);
    setStatus('üóëÔ∏è Storage cleared');
  };

  return (
    <div style={{
      position: 'fixed',
      top: '10px',
      right: '10px',
      background: '#f0f0f0',
      padding: '10px',
      border: '1px solid #ccc',
      borderRadius: '5px',
      fontSize: '12px',
      zIndex: 1000,
      maxWidth: '300px'
    }}>
      <div><strong>localStorage Status:</strong></div>
      <div>{status}</div>
      {notes.length > 0 && (
        <div>
          <div><strong>Stored Notes:</strong></div>
          {notes.slice(0, 3).map(note => (
            <div key={note.id} style={{ margin: '2px 0', fontSize: '11px' }}>
              ‚Ä¢ {note.title || 'Untitled'} ({note.isDeleted ? 'Deleted' : 'Active'})
            </div>
          ))}
          {notes.length > 3 && <div style={{ fontSize: '11px' }}>...and {notes.length - 3} more</div>}
        </div>
      )}
      <button 
        onClick={clearStorage}
        style={{
          marginTop: '5px',
          padding: '2px 5px',
          fontSize: '11px',
          background: '#ff6b6b',
          color: 'white',
          border: 'none',
          borderRadius: '3px',
          cursor: 'pointer'
        }}
      >
        Clear Storage
      </button>
    </div>
  );
};

export default LocalStorageTest;
