import React, { useState } from 'react';
import styles from './NoteEditor.module.css';

function NoteEditor({ note, onNoteUpdate }) {
  const [isPreviewMode, setIsPreviewMode] = useState(false);

  if (!note) {
    return (
      <div className={styles.editor}>
        <div className={styles.emptyState}>
          <h3>Select a note to start editing</h3>
          <p>Choose a note from the list or create a new one</p>
        </div>
      </div>
    );
  }

  const handleTitleChange = (e) => {
    onNoteUpdate({
      ...note,
      title: e.target.value,
      updatedAt: new Date().toISOString()
    });
  };

  const handleContentChange = (e) => {
    onNoteUpdate({
      ...note,
      content: e.target.value,
      updatedAt: new Date().toISOString()
    });
  };

  const handleToggleFavorite = () => {
    onNoteUpdate({
      ...note,
      isFavorite: !note.isFavorite,
      updatedAt: new Date().toISOString()
    });
  };

  const handleDownload = () => {
    const element = document.createElement('a');
    const file = new Blob([note.content], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `${note.title || 'Untitled'}.txt`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(note.content);
      // You could add a toast notification here
    } catch (err) {
      console.error('Failed to copy text: ', err);
    }
  };

  const handleSync = () => {
    // Sync functionality - for now just update the timestamp
    onNoteUpdate({
      ...note,
      updatedAt: new Date().toISOString()
    });
  };

  return (
    <div className={styles.editor}>
      <div className={styles.toolbar}>
        <input
          type="text"
          value={note.title}
          onChange={handleTitleChange}
          placeholder="Note title..."
          className={styles.titleInput}
        />
      </div>

      <textarea
        value={note.content}
        onChange={handleContentChange}
        placeholder="Start writing your note..."
        className={styles.contentArea}
      />

      <div className={styles.footer}>
        <span>Last updated: {new Date(note.updatedAt).toLocaleString()}</span>
      </div>
    </div>
  );
}

export default NoteEditor;
