import React, { useState } from 'react';
import styles from './Settings.module.css';
import { useTheme } from '../../context/ThemeContext';
import { useSettings } from '../../context/SettingsContext';

function Settings({ isOpen, onClose }) {
  const { isDarkTheme, toggleTheme } = useTheme();
  const { settings, toggleSetting } = useSettings();
  const [activeSection, setActiveSection] = useState('preferences');

  const handleDownloadAllNotes = () => {
    const notes = JSON.parse(localStorage.getItem('takenote-notes') || '[]');
    const zip = notes.map(note => ({
      filename: `${note.title || 'Untitled'}.md`,
      content: note.content
    }));

    // Create a simple text file with all notes for now
    const allNotesContent = notes.map(note => 
      `# ${note.title || 'Untitled'}\n\n${note.content}\n\n---\n\n`
    ).join('');

    const element = document.createElement('a');
    const file = new Blob([allNotesContent], { type: 'text/markdown' });
    element.href = URL.createObjectURL(file);
    element.download = 'all-notes.md';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const handleExportBackup = () => {
    const notes = localStorage.getItem('takenote-notes') || '[]';
    const settings = localStorage.getItem('takenote-settings') || '{}';
    const theme = localStorage.getItem('takenote-theme') || 'light';

    const backup = {
      notes: JSON.parse(notes),
      settings: JSON.parse(settings),
      theme,
      exportDate: new Date().toISOString()
    };

    const element = document.createElement('a');
    const file = new Blob([JSON.stringify(backup, null, 2)], { type: 'application/json' });
    element.href = URL.createObjectURL(file);
    element.download = `takenote-backup-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const handleImportBackup = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = (e) => {
      const file = e.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (event) => {
          try {
            const backup = JSON.parse(event.target.result);
            if (backup.notes) localStorage.setItem('takenote-notes', JSON.stringify(backup.notes));
            if (backup.settings) localStorage.setItem('takenote-settings', JSON.stringify(backup.settings));
            if (backup.theme) localStorage.setItem('takenote-theme', backup.theme);
            alert('Backup imported successfully! Please refresh the page.');
          } catch (error) {
            alert('Invalid backup file format.');
          }
        };
        reader.readAsText(file);
      }
    };
    input.click();
  };

  if (!isOpen) return null; 

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div className={styles.overlay} onClick={handleOverlayClick}>
      <div className={`${styles.modal} ${isDarkTheme ? styles.darkTheme : ''}`}>
        <div className={styles.header}>
          <div className={styles.userInfo}>
            <span className={styles.userName}>Demo User</span>
            <button className={styles.logoutButton}>Log out</button>
          </div>
          <button className={styles.closeButton} onClick={onClose}>
            âœ•
          </button>
        </div>

        <div className={styles.content}>
          <div className={styles.sidebar}>
            <div 
              className={`${styles.sidebarItem} ${activeSection === 'preferences' ? styles.active : ''}`}
              onClick={() => setActiveSection('preferences')}
            >
              <span className={styles.icon}>âš™</span>
              <span>Preferences</span>
            </div>
            <div 
              className={`${styles.sidebarItem} ${activeSection === 'shortcuts' ? styles.active : ''}`}
              onClick={() => setActiveSection('shortcuts')}
            >
              <span className={styles.icon}>âŒ¨</span>
              <span>Keyboard shortcuts</span>
            </div>
            <div 
              className={`${styles.sidebarItem} ${activeSection === 'data' ? styles.active : ''}`}
              onClick={() => setActiveSection('data')}
            >
              <span className={styles.icon}>ðŸ“Š</span>
              <span>Data management</span>
            </div>
            <div 
              className={`${styles.sidebarItem} ${activeSection === 'about' ? styles.active : ''}`}
              onClick={() => setActiveSection('about')}
            >
              <span className={styles.icon}>â„¹</span>
              <span>About TakeNote</span>
            </div>
          </div>

          <div className={styles.main}>
            <h2>Preferences</h2>

            <div className={styles.settingGroup}>
              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Active line highlight</h3>
                  <p>Controls whether the editor should highlight the active line</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.activeLineHighlight}
                    onChange={() => toggleSetting('activeLineHighlight')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Display line numbers</h3>
                  <p>Controls whether the editor should display line numbers</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.displayLineNumbers}
                    onChange={() => toggleSetting('displayLineNumbers')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Scroll past end</h3>
                  <p>Controls whether the editor will add blank space to the end of all files</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.scrollPastEnd}
                    onChange={() => toggleSetting('scrollPastEnd')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Markdown preview</h3>
                  <p>Controls whether markdown preview mode is enabled</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.markdownPreview}
                    onChange={() => toggleSetting('markdownPreview')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Dark mode</h3>
                  <p>Controls the application theme</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={isDarkTheme}
                    onChange={toggleTheme}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Settings;
