import React from 'react';
import styles from './Settings.module.css';
import { useTheme } from '../../context/ThemeContext';
import { useSettings } from '../../context/SettingsContext';

function Settings({ isOpen, onClose }) {
  const { isDarkTheme, toggleTheme } = useTheme();

  // Settings state
  const [settings, setSettings] = useState({
    activeLineHighlight: true,
    displayLineNumbers: false,
    scrollPastEnd: false,
    markdownPreview: true
  });

  // Load settings from localStorage on mount
  useEffect(() => {
    const savedSettings = localStorage.getItem('takenote-settings');
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }
  }, []);

  // Save settings to localStorage when they change
  useEffect(() => {
    localStorage.setItem('takenote-settings', JSON.stringify(settings));
  }, [settings]);

  const handleSettingChange = (settingName) => {
    setSettings(prev => ({
      ...prev,
      [settingName]: !prev[settingName]
    }));
  };

  if (!isOpen) return null; 

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div className={styles.overlay} onClick={handleOverlayClick}>
      <div className={`${styles.modal} ${isDarkTheme ? styles.darkTheme : ''}`}>
        <div className={styles.header}>
          <div className={styles.userInfo}>
            <span className={styles.userName}>Demo User</span>
            <button className={styles.logoutButton}>Log out</button>
          </div>
          <button className={styles.closeButton} onClick={onClose}>
            âœ•
          </button>
        </div>

        <div className={styles.content}>
          <div className={styles.sidebar}>
            <div className={`${styles.sidebarItem} ${styles.active}`}>
              <span className={styles.icon}>âš™</span>
              <span>Preferences</span>
            </div>
            <div className={styles.sidebarItem}>
              <span className={styles.icon}>âŒ¨</span>
              <span>Keyboard shortcuts</span>
            </div>
            <div className={styles.sidebarItem}>
              <span className={styles.icon}>ðŸ“Š</span>
              <span>Data management</span>
            </div>
            <div className={styles.sidebarItem}>
              <span className={styles.icon}>â„¹</span>
              <span>About TakeNote</span>
            </div>
          </div>

          <div className={styles.main}>
            <h2>Preferences</h2>

            <div className={styles.settingGroup}>
              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Active line highlight</h3>
                  <p>Controls whether the editor should highlight the active line</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.activeLineHighlight}
                    onChange={() => handleSettingChange('activeLineHighlight')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Display line numbers</h3>
                  <p>Controls whether the editor should display line numbers</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.displayLineNumbers}
                    onChange={() => handleSettingChange('displayLineNumbers')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Scroll past end</h3>
                  <p>Controls whether the editor will add blank space to the end of all files</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.scrollPastEnd}
                    onChange={() => handleSettingChange('scrollPastEnd')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Markdown preview</h3>
                  <p>Controls whether markdown preview mode is enabled</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={settings.markdownPreview}
                    onChange={() => handleSettingChange('markdownPreview')}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>

              <div className={styles.settingItem}>
                <div className={styles.settingInfo}>
                  <h3>Dark mode</h3>
                  <p>Controls the application theme</p>
                </div>
                <label className={styles.toggle}>
                  <input 
                    type="checkbox" 
                    checked={isDarkTheme}
                    onChange={toggleTheme}
                  />
                  <span className={styles.slider}></span>
                </label>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Settings;
