import React, { useState } from 'react';
import styles from './NoteEditor.module.css';

function NoteEditor({ note, onNoteUpdate }) {
  const [isPreview, setIsPreview] = useState(false);
  const [showOptions, setShowOptions] = useState(false);

  if (!note) {
    return (
      <div className={styles.editor}>
        <div className={styles.emptyState}>
          <h3>Select a note to start editing</h3>
          <p>Choose a note from the list or create a new one</p>
        </div>
      </div>
    );
  }

  const handleTitleChange = (e) => {
    onNoteUpdate({
      ...note,
      title: e.target.value,
      updatedAt: new Date().toISOString()
    });
  };

  const handleContentChange = (e) => {
    onNoteUpdate({
      ...note,
      content: e.target.value,
      updatedAt: new Date().toISOString()
    });
  };

  const formatText = (format) => {
    const textarea = document.querySelector(`.${styles.contentArea}`);
    if (!textarea) return;

    const start = textarea.selectionStart;
    const end = textarea.selectionEnd;
    const selectedText = note.content.substring(start, end);

    let formattedText = '';
    let newCursorPos = start;

    switch (format) {
      case 'bold':
        formattedText = `**${selectedText}**`;
        newCursorPos = start + (selectedText ? 2 : 2);
        break;
      case 'italic':
        formattedText = `*${selectedText}*`;
        newCursorPos = start + (selectedText ? 1 : 1);
        break;
      case 'heading':
        formattedText = `# ${selectedText}`;
        newCursorPos = start + (selectedText ? 2 : 2);
        break;
      case 'bullet':
        formattedText = `- ${selectedText}`;
        newCursorPos = start + (selectedText ? 2 : 2);
        break;
      case 'code':
        formattedText = `\`${selectedText}\``;
        newCursorPos = start + (selectedText ? 1 : 1);
        break;
      default:
        return;
    }

    const newContent = note.content.substring(0, start) + formattedText + note.content.substring(end);

    onNoteUpdate({
      ...note,
      content: newContent,
      updatedAt: new Date().toISOString()
    });

    setTimeout(() => {
      textarea.focus();
      textarea.setSelectionRange(newCursorPos, newCursorPos + (selectedText ? selectedText.length : 0));
    }, 0);
  };

  const renderPreview = () => {
    let html = note.content
      .replace(/^# (.*$)/gim, '<h1>$1</h1>')
      .replace(/^## (.*$)/gim, '<h2>$1</h2>')
      .replace(/^### (.*$)/gim, '<h3>$1</h3>')
      .replace(/\*\*(.*)\*\*/gim, '<strong>$1</strong>')
      .replace(/\*(.*)\*/gim, '<em>$1</em>')
      .replace(/`(.*)`/gim, '<code>$1</code>')
      .replace(/^- (.*$)/gim, '<li>$1</li>')
      .replace(/\n/gim, '<br>');

    if (html.includes('<li>')) {
      html = html.replace(/(<li>.*<\/li>)/s, '<ul>$1</ul>');
    }

    return { __html: html };
  };

  return (
    <div className={styles.editor}>
      <div className={styles.toolbar}>
        <input
          type="text"
          value={note.title}
          onChange={handleTitleChange}
          placeholder="Note title..."
          className={styles.titleInput}
        />

        <div className={styles.editorActions}>
          <div className={styles.formatButtons}>
            <button
              onClick={() => formatText('bold')}
              className={styles.formatButton}
              title="Bold (Ctrl+B)"
            >
              <strong>B</strong>
            </button>
            <button
              onClick={() => formatText('italic')}
              className={styles.formatButton}
              title="Italic (Ctrl+I)"
            >
              <em>I</em>
            </button>
            <button
              onClick={() => formatText('heading')}
              className={styles.formatButton}
              title="Heading"
            >
              H1
            </button>
            <button
              onClick={() => formatText('bullet')}
              className={styles.formatButton}
              title="Bullet List"
            >
              •
            </button>
            <button
              onClick={() => formatText('code')}
              className={styles.formatButton}
              title="Code"
            >
              {'</>'}
            </button>
          </div>

          <div className={styles.viewButtons}>
            <button
              onClick={() => setIsPreview(false)}
              className={`${styles.viewButton} ${!isPreview ? styles.active : ''}`}
              title="Edit Mode"
            >
              ✏️ Edit
            </button>
            <button
              onClick={() => setIsPreview(true)}
              className={`${styles.viewButton} ${isPreview ? styles.active : ''}`}
              title="Preview Mode"
            >
              👁️ Preview
            </button>
          </div>

          <button
            onClick={() => setShowOptions(!showOptions)}
            className={styles.optionsButton}
            title="More Options"
          >
            ⋮
          </button>

          {showOptions && (
            <div className={styles.optionsDropdown}>
              <button
                onClick={() => {
                  navigator.clipboard.writeText(note.content);
                  setShowOptions(false);
                }}
                className={styles.optionItem}
              >
                📋 Copy Content
              </button>
              <button
                onClick={() => {
                  const blob = new Blob([note.content], { type: 'text/plain' });
                  const url = URL.createObjectURL(blob);
                  const a = document.createElement('a');
                  a.href = url;
                  a.download = `${note.title || 'note'}.txt`;
                  a.click();
                  URL.revokeObjectURL(url);
                  setShowOptions(false);
                }}
                className={styles.optionItem}
              >
                💾 Download as TXT
              </button>
              <button
                onClick={() => {
                  const wordCount = note.content.split(/\s+/).filter(word => word).length;
                  const charCount = note.content.length;
                  alert(`Word count: ${wordCount}\nCharacter count: ${charCount}`);
                  setShowOptions(false);
                }}
                className={styles.optionItem}
              >
                📊 Word Count
              </button>
            </div>
          )}
        </div>
      </div>

      {isPreview ? (
        <div className={styles.previewArea}>
          <div 
            className={styles.previewContent}
            dangerouslySetInnerHTML={renderPreview()}
          />
        </div>
      ) : (
        <textarea
          value={note.content}
          onChange={handleContentChange}
          placeholder="Start writing your note..."
          className={styles.contentArea}
        />
      )}

      <div className={styles.footer}>
        <span>Last updated: {new Date(note.updatedAt).toLocaleString()}</span>
        <span className={styles.stats}>
          {note.content.split(/\s+/).filter(word => word).length} words • {note.content.length} characters
        </span>
      </div>
    </div>
  );
}

export default NoteEditor;
