import React, { useState } from 'react';
import styles from './NoteEditor.module.css';

function NoteEditor({ note, onNoteUpdate }) {
  const [isPreview, setIsPreview] = useState(false);
  const [showOptions, setShowOptions] = useState(false);

  if (!note) {
    return (
      <div className={styles.editor}>
        <div className={styles.emptyState}>
          <h3>Select a note to start editing</h3>
          <p>Choose a note from the list or create a new one</p>
        </div>
      </div>
    );
  }

  const handleTitleChange = (e) => {
    const updatedNote = { ...note, title: e.target.value, updatedAt: new Date().toISOString() };
    onNoteUpdate(updatedNote);
  };

  const handleContentChange = (e) => {
    const updatedNote = { ...note, content: e.target.value, updatedAt: new Date().toISOString() };
    onNoteUpdate(updatedNote);
  };

  const renderPreview = () => {
    let html = note.content
      .replace(/^### (.*$)/gim, '<h3>$1</h3>')
      .replace(/^## (.*$)/gim, '<h2>$1</h2>')
      .replace(/^# (.*$)/gim, '<h1>$1</h1>')
      .replace(/^\> (.*$)/gim, '<blockquote>$1</blockquote>')
      .replace(/\*\*(.*)\*\*/gim, '<strong>$1</strong>')
      .replace(/\*(.*)\*/gim, '<em>$1</em>')
      .replace(/!\[([^\]]*)\]\(([^\)]+)\)/gim, '<img alt="$1" src="$2" />')
      .replace(/\[([^\]]+)\]\(([^\)]+)\)/gim, '<a href="$2">$1</a>')
      .replace(/\n$/gim, '<br>');

    return { __html: html };
  };

  const downloadNote = () => {
    const blob = new Blob([note.content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${note.title || 'note'}.txt`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(note.content);
  };

  const toggleFavorite = () => {
    const updatedNote = { ...note, favorite: !note.favorite, updatedAt: new Date().toISOString() };
    onNoteUpdate(updatedNote);
  };

  const wordCount = note.content.split(/\s+/).filter(word => word).length;
  const charCount = note.content.length;
  const lineCount = note.content.split('\n').length;

  return (
    <div className={styles.editor}>
      <div className={styles.toolbar}>
        <input
          type="text"
          value={note.title}
          onChange={handleTitleChange}
          placeholder="Note title..."
          className={styles.titleInput}
        />
      </div>

      <div className={styles.noteMenuBar}>
        <div className={styles.noteMenuBarLeft}>
          <button
            onClick={() => setIsPreview(false)}
            className={`${styles.noteMenuButton} ${!isPreview ? styles.active : ''}`}
            title="Edit Mode"
          >
            ‚úèÔ∏è
          </button>
          <button
            onClick={() => setIsPreview(true)}
            className={`${styles.noteMenuButton} ${isPreview ? styles.active : ''}`}
            title="Preview Mode"
          >
            üëÅÔ∏è
          </button>
          <button
            onClick={toggleFavorite}
            className={`${styles.noteMenuButton} ${note.favorite ? styles.active : ''}`}
            title="Toggle Favorite"
          >
            ‚≠ê
          </button>
          <button
            onClick={downloadNote}
            className={styles.noteMenuButton}
            title="Download Note"
          >
            ‚¨áÔ∏è
          </button>
          <button
            onClick={copyToClipboard}
            className={styles.noteMenuButton}
            title="Copy to Clipboard"
          >
            üìã
          </button>
        </div>

        <div className={styles.noteMenuBarRight}>
          <span className={styles.noteStats}>
            {lineCount} lines ‚Ä¢ {wordCount} words ‚Ä¢ {charCount} characters
          </span>
        </div>
      </div>

      {isPreview ? (
        <div className={styles.previewArea}>
          <div 
            className={styles.previewContent}
            dangerouslySetInnerHTML={renderPreview()}
          />
        </div>
      ) : (
        <textarea
          value={note.content}
          onChange={handleContentChange}
          placeholder="Start writing your note..."
          className={styles.contentArea}
          spellCheck="false"
        />
      )}
    </div>
  );
}

export default NoteEditor;
