import React, { useState } from 'react';
import Sidebar from './components/Sidebar/Sidebar';
import NoteList from './components/NoteList/NoteList';
import NoteEditor from './components/NoteEditor/NoteEditor';
import useNotes from './hooks/useNotes';
import './App.css';

function App() {
  // Using custom hook to manage notes
  const {
    selectedNote,
    setSelectedNote,
    createNote,
    updateNote,
    deleteNote,
    restoreNote,
    permanentDeleteNote,
    toggleFavorite,
    getFilteredNotes,
    getNoteCounts
  } = useNotes();

  // State for current category and search
  const [activeCategory, setActiveCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  // Handle creating new note
  const handleNewNote = () => {
    createNote();
  };

  // Handle selecting a note
  const handleNoteSelect = (note) => {
    setSelectedNote(note);
  };

  // Handle category selection
  const handleCategorySelect = (category) => {
    setActiveCategory(category);
    setSelectedNote(null); // Clear selected note when switching categories
  };

  // Get notes based on active category
  const filteredNotes = getFilteredNotes(activeCategory);
  const noteCounts = getNoteCounts();

  return (
    <div className="App">
      <Sidebar
        activeCategory={activeCategory}
        onCategorySelect={handleCategorySelect}
        noteCounts={noteCounts}
      />

      <NoteList
        notes={filteredNotes}
        selectedNote={selectedNote}
        onNoteSelect={handleNoteSelect}
        onNewNote={handleNewNote}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
      />

      <NoteEditor
        note={selectedNote}
        onNoteUpdate={updateNote}
        onNoteDelete={deleteNote}
        onNoteFavorite={toggleFavorite}
        onNoteRestore={restoreNote}
        onNotePermanentDelete={permanentDeleteNote}
      />
    </div>
  );
}

export default App;
