import React from 'react';
import styles from './TextArea.module.css';

const TextArea = ({ 
  placeholder = '',
  value,
  onChange,
  className = '',
  rows = 4,
  resize = true,
  ...props 
}) => {
  const textareaClass = `${styles.textarea} ${!resize ? styles.noResize : ''} ${className}`;

  return (
    <textarea
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      className={textareaClass}
      rows={rows}
      {...props}
    />
  );
};

export default TextArea;
