import React from 'react';
import { Button, Input, Icon } from '../../atoms';
import styles from './Toolbar.module.css';

const Toolbar = ({ 
  title = '',
  onTitleChange,
  isPreviewMode = false,
  onPreviewToggle,
  onSettingsClick,
  onThemeToggle,
  isDarkTheme = false,
  className = ''
}) => {
  return (
    <div className={`${styles.toolbar} ${isDarkTheme ? styles.darkTheme : ''} ${className}`}>
      <Input
        type="text"
        value={title}
        onChange={onTitleChange}
        placeholder="Note title..."
        className={styles.titleInput}
        fullWidth
      />

      <div className={styles.toolbarActions}>
        <Button
          onClick={onPreviewToggle}
          variant="ghost"
          size="small"
          title={isPreviewMode ? "Edit" : "Preview"}
        >
          <Icon name={isPreviewMode ? 'edit' : 'preview'} size="small" />
        </Button>

        <Button
          onClick={onThemeToggle}
          variant="ghost"
          size="small"
          title="Toggle theme"
        >
          <Icon name={isDarkTheme ? 'sun' : 'moon'} size="small" />
        </Button>

        <Button
          onClick={onSettingsClick}
          variant="ghost"
          size="small"
          title="Settings"
        >
          <Icon name="settings" size="small" />
        </Button>
      </div>
    </div>
  );
};

export default Toolbar;
