import React, { useState } from 'react';
import { AppLayout } from '../components/templates';
import { NoteList, NoteEditor, Settings } from '../components/organisms';
import useNotes from '../hooks/useNotes';
import { useTheme } from '../context/ThemeContext';
import { useSettings } from '../context/SettingsContext';

const NotesPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  const { 
    notes, 
    selectedNote, 
    setSelectedNote, 
    createNote, 
    updateNote, 
    toggleFavorite 
  } = useNotes();
  const { isDarkTheme, toggleTheme } = useTheme();
  const { settings, toggleSetting } = useSettings();

  React.useEffect(() => {
    setIsPreviewMode(settings.markdownPreview);
  }, [settings.markdownPreview]);

  const selectedNoteId = selectedNote?.id || null;

  const handleNoteCreate = () => {
    createNote();
  };

  const handleNoteSelect = (noteId) => {
    const note = notes.find(n => n.id === noteId);
    setSelectedNote(note);
  };

  const handleNoteUpdate = (noteId, updates) => {
    updateNote(noteId, updates);
  };

  const handlePreviewToggle = () => {
    const newPreviewMode = !isPreviewMode;
    setIsPreviewMode(newPreviewMode);
    toggleSetting('markdownPreview');
  };

  const handleDownloadAllNotes = () => {
    const allNotesContent = notes.map(note => 
      `# ${note.title || 'Untitled'}\n\n${note.content}\n\n---\n\n`
    ).join('');

    const element = document.createElement('a');
    const file = new Blob([allNotesContent], { type: 'text/markdown' });
    element.href = URL.createObjectURL(file);
    element.download = 'all-notes.md';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  const handleNoteMenuClick = (noteId) => {
    // Handle note menu actions (delete, duplicate, etc.)
    console.log('Note menu clicked for:', noteId);
  };

  return (
    <AppLayout
      isDarkTheme={isDarkTheme}
      sidebar={
        <NoteList
          notes={notes}
          selectedNoteId={selectedNoteId}
          searchTerm={searchTerm}
          onSearchChange={setSearchTerm}
          onNoteSelect={handleNoteSelect}
          onNoteCreate={handleNoteCreate}
          onNoteStarToggle={toggleFavorite}
          onNoteMenuClick={handleNoteMenuClick}
          isDarkTheme={isDarkTheme}
        />
      }
      main={
        <NoteEditor
          note={selectedNote}
          onNoteUpdate={handleNoteUpdate}
          isPreviewMode={isPreviewMode}
          onPreviewToggle={handlePreviewToggle}
          onSettingsClick={() => setIsSettingsOpen(true)}
          onThemeToggle={toggleTheme}
          isDarkTheme={isDarkTheme}
          displayLineNumbers={settings.displayLineNumbers}
        />
      }
      modal={
        <Settings
          isOpen={isSettingsOpen}
          onClose={() => setIsSettingsOpen(false)}
          settings={settings}
          onSettingToggle={toggleSetting}
          onDownloadNotes={handleDownloadAllNotes}
          isDarkTheme={isDarkTheme}
        />
      }
    />
  );
};

export default NotesPage;
