import React from 'react';
import styles from './Icon.module.css';

const Icon = ({ 
  name, 
  size = 'medium', 
  color = 'default',
  className = '',
  onClick,
  ...props 
}) => {
  const iconClass = `${styles.icon} ${styles[size]} ${styles[color]} ${className}`;

  const getIconContent = () => {
    switch (name) {
      case 'edit': return '‚úèÔ∏è';
      case 'preview': return 'üëÅÔ∏è';
      case 'settings': return '‚öôÔ∏è';
      case 'theme': return 'üåô';
      case 'sun': return '‚òÄÔ∏è';
      case 'moon': return 'üåô';
      case 'star': return '‚òÜ';
      case 'star-filled': return '‚≠ê';
      case 'star-outline': return '‚òÜ';
      case 'menu': return '‚ãÆ';
      case 'close': return '‚úï';
      case 'search': return 'üîç';
      case 'plus': return '+';
      case 'download': return '‚¨áÔ∏è';
      case 'upload': return '‚¨ÜÔ∏è';
      case 'trash': return 'üóëÔ∏è';
      case 'copy': return 'üìã';
      case 'duplicate': return 'üìÑ';
      default: return name;
    }
  };

  return (
    <span 
      className={iconClass}
      onClick={onClick}
      {...props}
    >
      {getIconContent()}
    </span>
  );
};

export default Icon;
