import React from 'react';
import { Button, Icon } from '../../atoms';
import styles from './EditorFooter.module.css';

const EditorFooter = ({ 
  note,
  onDeleteNote,
  onDuplicateNote,
  onToggleFavorite,
  onCopyNote,
  isDarkTheme = false,
  displayLineNumbers = false,
  cursorPosition = { line: 1, column: 1 },
  className = ''
}) => {
  const handleCopyNote = () => {
    if (note?.content) {
      navigator.clipboard.writeText(note.content);
      // You could add a toast notification here
    }
    if (onCopyNote) onCopyNote();
  };

  const handleDeleteNote = () => {
    if (note && onDeleteNote) {
      onDeleteNote(note.id);
    }
  };

  const handleDuplicateNote = () => {
    if (note && onDuplicateNote) {
      onDuplicateNote(note.id);
    }
  };

  const handleToggleFavorite = () => {
    if (note && onToggleFavorite) {
      onToggleFavorite(note.id);
    }
  };

  if (!note) return null;

  return (
    <div className={`${styles.footer} ${isDarkTheme ? styles.darkTheme : ''} ${className}`}>
      <div className={styles.leftSection}>
        {displayLineNumbers && (
          <span className={styles.cursorInfo}>
            Line {cursorPosition.line}, Column {cursorPosition.column}
          </span>
        )}
      </div>

      <div className={styles.rightSection}>
        <Button
          onClick={handleCopyNote}
          variant="ghost"
          size="small"
          title="Copy note content"
          className={styles.footerButton}
        >
          <Icon name="copy" size="small" />
        </Button>

        <Button
          onClick={handleDuplicateNote}
          variant="ghost"
          size="small"
          title="Duplicate note"
          className={styles.footerButton}
        >
          <Icon name="duplicate" size="small" />
        </Button>

        <Button
          onClick={handleToggleFavorite}
          variant="ghost"
          size="small"
          title={note.isFavorite ? "Remove from favorites" : "Add to favorites"}
          className={`${styles.footerButton} ${note.isFavorite ? styles.favoriteActive : ''}`}
        >
          <Icon name={note.isFavorite ? "star-filled" : "star"} size="small" />
        </Button>

        <Button
          onClick={handleDeleteNote}
          variant="ghost"
          size="small"
          title="Delete note"
          className={`${styles.footerButton} ${styles.deleteButton}`}
        >
          <Icon name="trash" size="small" />
        </Button>
      </div>
    </div>
  );
};

export default EditorFooter;
