import React, { useState, useRef, useEffect } from 'react';
import { TextArea } from '../atoms';
import { Toolbar } from '../molecules';
import styles from './NoteEditor.module.css';

const NoteEditor = ({ 
  note,
  onNoteUpdate,
  isPreviewMode = false,
  onPreviewToggle,
  onSettingsClick,
  onThemeToggle,
  isDarkTheme = false,
  displayLineNumbers = false,
  className = ''
}) => {
  const [cursorPosition, setCursorPosition] = useState({ line: 1, column: 1 });
  const textareaRef = useRef(null);

  useEffect(() => {
    const updateCursorPosition = () => {
      if (textareaRef.current && displayLineNumbers) {
        const textarea = textareaRef.current;
        const text = textarea.value;
        const cursorPos = textarea.selectionStart;
        const textBeforeCursor = text.substring(0, cursorPos);
        const line = textBeforeCursor.split('\n').length;
        const lastLineStart = textBeforeCursor.lastIndexOf('\n') + 1;
        const column = cursorPos - lastLineStart + 1;
        setCursorPosition({ line, column });
      }
    };

    if (textareaRef.current) {
      const textarea = textareaRef.current;
      textarea.addEventListener('keyup', updateCursorPosition);
      textarea.addEventListener('click', updateCursorPosition);
      return () => {
        textarea.removeEventListener('keyup', updateCursorPosition);
        textarea.removeEventListener('click', updateCursorPosition);
      };
    }
  }, [displayLineNumbers, note?.content]);

  const handleTitleChange = (e) => {
    if (note && onNoteUpdate) {
      onNoteUpdate(note.id, { title: e.target.value });
    }
  };

  const handleContentChange = (e) => {
    if (note && onNoteUpdate) {
      onNoteUpdate(note.id, { content: e.target.value });
    }
  };

  const renderPreview = () => {
    if (!note?.content) return <div className={styles.emptyPreview}>No content to preview</div>;

    return (
      <div className={styles.preview}>
        <div 
          className={styles.previewContent}
          dangerouslySetInnerHTML={{ 
            __html: note.content.replace(/\n/g, '<br>') 
          }}
        />
      </div>
    );
  };

  if (!note) {
    return (
      <div className={`${styles.editor} ${isDarkTheme ? styles.darkTheme : ''} ${className}`}>
        <div className={styles.emptyState}>
          <h3>No note selected</h3>
          <p>Select a note from the sidebar or create a new one</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`${styles.editor} ${isDarkTheme ? styles.darkTheme : ''} ${className}`}>
      <Toolbar
        title={note.title}
        onTitleChange={handleTitleChange}
        isPreviewMode={isPreviewMode}
        onPreviewToggle={onPreviewToggle}
        onSettingsClick={onSettingsClick}
        onThemeToggle={onThemeToggle}
        isDarkTheme={isDarkTheme}
        className={`${isDarkTheme ? 'darkTheme' : ''}`}
      />

      {isPreviewMode ? (
        renderPreview()
      ) : (
        <div className={styles.editorContainer}>
          {displayLineNumbers && (
            <div className={styles.lineNumbers}>
              {note.content.split('\n').map((_, index) => (
                <div key={index} className={styles.lineNumber}>
                  {index + 1}
                </div>
              ))}
            </div>
          )}
          <TextArea
            ref={textareaRef}
            value={note.content}
            onChange={handleContentChange}
            placeholder="Start writing your note..."
            className={`${styles.contentArea} ${isDarkTheme ? 'darkTheme' : ''}`}
            style={{
              paddingLeft: displayLineNumbers ? '60px' : '20px'
            }}
            resize={false}
          />
        </div>
      )}

      {displayLineNumbers && !isPreviewMode && (
        <div className={styles.footer}>
          <span className={styles.cursorInfo}>
            Line {cursorPosition.line}, Column {cursorPosition.column}
          </span>
        </div>
      )}
    </div>
  );
};

export default NoteEditor;
